import React, { Component } from 'react'

import ReactDOM from "react-dom";

import TMDB from '../TMDB'

export default class FilmRow extends Component {
    render() {

        const filmTitle = TMDB.films.map(ele => {
            return ele.title;

        })

        const filmYear = TMDB.films.map(ele => {
            return ele.release_date;

        })

        const filmImage = TMDB.films.map(ele => {
            return ele.poster_path
        })
        return (
            <div>
                <div className="film-row">
                    {filmImage.map ((ele,i) => (
                        <div key={i}>
                             <img src={ele} alt="" />
                        </div>
                    ))}

                <div className="film-summary">
                    {filmTitle.map((ele, i) => (
                          <h1>{ele}</h1>

                    ))}
                   {filmYear.map((ele, i) => (
                          <p>{(new Date(ele).getFullYear())}</p>
                    ))}

                </div>
                </div>
            </div>
        )
    }
}
