export default class FilmRow extends Component {
    render() {

        return (
            <div>
                <div className="film-row">
                    {filmImage.map ((ele,i) => (
                        <div key={i}>
                             <img src={ele} alt="" />
                        </div>
                    ))}

                <div className="film-summary">
                    {filmTitle.map((ele, i) => (
                          <h1>{ele}</h1>

                    ))}
                   {filmYear.map((ele, i) => (
                          <p>{(new Date(ele).getFullYear())}</p>
                    ))}

                </div>
                </div>
            </div>
        )
    }
}
